{
	brush->r = gtk_adjustment_get_value(slider) * 0.5;
	BrushCoreSetGrayCirclePattern(brush->core, brush->r, brush->outline_hardness * 0.01,
		brush->blur * 0.01, brush->alpha * 0.01);
}

