		smudge->opacity = IniFileGetInteger(file, section_name, "OPACITY");
		smudge->blur = IniFileGetInteger(file, section_name, "BLUR");
		smudge->outline_hardness = IniFileGetInteger(file, section_name, "OUTLINE_HARDNESS");
